import React, { useState } from "react";
import arrow from "../../assest/arrow.png";
import "./slidebar.scss";
const Slidebar = ({top5}) => {
    const data = top5;
    console.log(data)
    const [currentSlide, setCurrentSlide] = useState(0);
    const handleClikc = (way) => {
        way === "left" ? setCurrentSlide(currentSlide > 0 ? currentSlide -1 : 2) : 
        setCurrentSlide(currentSlide < data.length - 1 ? currentSlide + 1 : 0);
    }
  return (
    <div className="sliderbar">
      <div className="slider" style={{transform:`translateX(-${currentSlide * 100}vw)`}}>
        {top5.map ((d) => (<div className="container">
          <div className="item">
            <div className="left">
              <div className="leftContainer">
                  <img src="something" alt="" />
              </div>
              <h2>Title</h2>
              <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Maxime, eligendi.</p>
              <span>Projects</span>
            </div>
            <div className="right">
                <img src="asd" alt="" />
            </div>
          </div>
        </div> ))}
      </div>
      <img src={arrow} alt="" className="arrow left" onClick={()=> handleClikc("left")}/>
      <img src={arrow} alt="" className="arrow right" onClick={()=> handleClikc("right")}/>
    </div>
  );
};

export default Slidebar;
